$(document).ready(function () {
	$(".popup-send").magnificPopup(),
	$(".owl-carousel").owlCarousel({
		loop : !0,
		margin : 10,
		items : 1
	}),
	setTimeout(function () {
		$(".video-reviews").css("display", "block"),
		$(".audio-player").css("display", "block")
	}, 200),
	$(window).width() > 479 ? plyr.setup({
		volume : "10",
		i18n : {
			restart : "Перезапустить",
			rewind : "Перемотка {seektime} секунд",
			play : "Воспроизвести",
			pause : "Пауза",
			forward : "Вперед",
			buffered : "Буфферизация",
			currentTime : "Текущее время",
			duration : "Продолжительность",
			volume : "Звук",
			toggleMute : "Без звука",
			toggleCaptions : "Заголовок",
			toggleFullscreen : "Полный экран"
		}
	}) : plyr.setup({
		controls : ["play", "volume", "fullscreen"],
		volume : "10",
		i18n : {
			restart : "",
			rewind : "",
			play : "",
			pause : "",
			forward : "",
			buffered : "",
			currentTime : "",
			duration : "",
			volume : "",
			toggleMute : "",
			toggleCaptions : "",
			toggleFullscreen : ""
		}
	});
});